 val intent = Intent().apply {
 flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK
 action = Settings.ACTION_REQUEST_SCHEDULE_EXACT_ALARM
 }
 context.startActivity(intent)
 onCloseDialog()
 }
 },
 )
 MathAlarmDialog(
